import React, { Component, Fragment } from "react";

class Product extends Component {
  state = {
    order: 10,
  };

  handlePlus = () => {
    this.setState({
      order: (this.state.order += 1),
    });
  };

  render() {
    return (
      <Fragment>
        <div className="header">
          <div className="logo">
            <img
              src="https://kampusmerdeka.kemdikbud.go.id/static/media/logo-pendidikan.92aef756.webp"
              alt="Kemdikbud"
            />
          </div>
          <div className="troley">
            {/* <img src="https://img2.pngdownload.id/20180528/gf/kisspng-shopping-bags-trolleys-shopping-cart-logo-clip-a-online-shop-5b0c34bb57cc75.7415233215275265873596.jpg" /> */}
            <div className="count">{this.state.order}</div>
          </div>
        </div>
        <div className="card">
          <div className="img-thumb-prod">
            <img
              src="https://ds393qgzrxwzn.cloudfront.net/resize/m600x500/cat1/img/images/0/SmKmuPrbYP.jpg"
              alt=""
            />
          </div>
          <div className="product-title">barang-barang</div>
          <div className="product-price">Rp 410.000</div>
          <div className="counter">
            <button className="minus" onClick={this.handlePlus}>
              +
            </button>
            <input type="text" value={this.state.order} />
            <button className="plus">-</button>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default Product;
