  //     axios.get(`http://localhost:3001/posts/${id}`).then((DataAPI) => {
  //       //   console.log(DataAPI.data);
  //       setState({ name: DataAPI.data.title }, { body: DataAPI.data.body });
  //     });
  //   }
  console.log(meta.body);
  return (
    <Fragment>
      <h2>{`Judul: ${meta.title}`}</h2>
      <p>{`Content: ${meta.body}`}</p>
    </Fragment>
  );
  //   <div>DetailPost</div>;
}

