import React, { Component, Fragment } from "react";
import "./BlogPost.css";
import Post from "../../components/Post/Post";
import axios from "axios";

class BlogPost extends Component {
  state = {
    post: [],
  };

  getPostDataAPI() {
    axios.get("http://localhost:3001/posts").then((DataAPI) => {
      console.log(DataAPI.data);
      this.setState({
        post: DataAPI.data,
      });
    });
  }

  removeDataHandler(data) {
    axios.delete(`http://localhost:3001/posts/${data}`).then((result) => {
      this.getPostDataAPI();
      console.log(result);
    });

    // console.log(data);
  }

  componentWillMount() {
    this.getPostDataAPI();
  }

  // Fetch API with fetch
  // fetch("https://jsonplaceholder.typicode.com/posts")
  //   .then((Response) => Response.json())
  //   .then((json) => {
  //     this.setState({
  //       post: json,
  //     });
  //   });
  //   fetch API with Axios
  // axios.get("http://localhost:3001/posts").then((DataAPI) => {
  //   console.log(DataAPI.data);
  //   this.setState({
  //     post: DataAPI.data,
  //   });
  // });

  render() {
    return (
      <Fragment>
        {this.state.post.map((post) => {
          return (
            <Post
              key={post.id}
              data={post}
              remove={this.removeDataHandler}
              choose={this.getPostDataAPI}
            />
          );
        })}
        {/* {console.log(this.state.post)} */}
      </Fragment>
    );
  }
}

export default BlogPost;
