import React from "react";
import { useParams } from "react-router-dom";

export default function DetailPost() {
  let { id } = useParams();
  //   console.log(props);
  function get() {
    axios
      .get("http://localhost:3001/posts?_sort=id&_order=desc")
      .then((DataAPI) => {
        // console.log(DataAPI.data);
        this.setState({
          post: DataAPI.data,
        });
      });
  }
  return <div>{id}</div>;
  //   <div>DetailPost</div>;
}
