import React from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function DetailPost() {
  let { id } = useParams();
  //   console.log(props);
  function get(ID) {
    axios.get(`http://localhost:3001/posts/${id}`).then((DataAPI) => {
      // console.log(DataAPI.data);
      this.setState({
        post: DataAPI.data,
      });
    });
  }
  return <div>{get()}</div>;
  //   <div>DetailPost</div>;
}
