import React, { Fragment, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function DetailPost() {
  const [GetState, setState] = useState([
    {
      name: "",
      body: "",
    },
  ]);
  const testing = console.log(GetState);
  let { id } = useParams();
  //   console.log(props);

  useEffect(() => {
    const result = axios.get(`http://localhost:3001/posts/${id}`);
    setState(result.GetState);
  });

  function get(ID) {
    axios.get(`http://localhost:3001/posts/${id}`).then((DataAPI) => {
      //   console.log(DataAPI.data);
      setState({ name: DataAPI.data.title }, { body: DataAPI.data.body });
    });
  }
  return (
    <Fragment>
      <h2>asd</h2>
    </Fragment>
  );
  //   <div>DetailPost</div>;
}
