import React from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function DetailPost() {
  const state = {
    post: [],
    formBlogPost: {
      id: 1,
      title: "",
      body: "",
      userId: 1,
    },
  };
  let { id } = useParams();
  //   console.log(props);
  function get(ID) {
    axios.get(`http://localhost:3001/posts/${id}`).then((DataAPI) => {
      // console.log(DataAPI.data);
      this.state({
        post: DataAPI.data,
      });
    });
  }
  return <div>{get()}</div>;
  //   <div>DetailPost</div>;
}
