import React, { Component, Fragment } from "react";
import "./BlogPost.css";
import Post from "../../components/Post/Post";
import axios from "axios";

class BlogPost extends Component {
  state = {
    post: [],
  };

  getPostDataAPI = async () => {
    axios.get("http://localhost:3001/posts").then((DataAPI) => {
      console.log(DataAPI.data);
      this.setState({
        post: DataAPI.data,
      });
    });
  };

  removeDataHandler = (data) => {
    axios.delete(`http://localhost:3001/posts/${data}`).then((res) => {
      console.log(res);
      console.log(this.state.post);
      this.getPostDataAPI();
    });

    // console.log(data);
  };
  // removeDataHandler(data, e) {
  //   axios.delete(`http://localhost:3001/posts/${data}`).then((res) => {
  //     console.log(res);
  //     console.log(this.state.post);
  //     //getPostDataAPI();
  //   });

  //   // console.log(data);
  // }

  componentWillMount() {
    this.getPostDataAPI();
  }

  // Fetch API with fetch
  // fetch("https://jsonplaceholder.typicode.com/posts")
  //   .then((Response) => Response.json())
  //   .then((json) => {
  //     this.setState({
  //       post: json,
  //     });
  //   });
  //   fetch API with Axios
  // axios.get("http://localhost:3001/posts").then((DataAPI) => {
  //   console.log(DataAPI.data);
  //   this.setState({
  //     post: DataAPI.data,
  //   });
  // });

  render() {
    return (
      <Fragment>
        <p className="section-title">Blog Post</p>
        <div className="form-add-post">
          <label htmlFor="title">Title</label>
          <input type="text" name="title" placeholder="add Title" />
          <label htmlFor="body-content">Blog Content</label>
          <textarea
            name="body-content"
            id="body-content"
            cols="30"
            rows="10"
            placeholder="add Blog Content"
          />
          <button className="btn-submit">Simpan</button>
        </div>
        {this.state.post.map((post) => {
          return (
            <Post
              key={post.id}
              data={post}
              remove={this.removeDataHandler}
              choose={this.getPostDataAPI}
            />
          );
        })}
        {/* {console.log(this.state.post)} */}
      </Fragment>
    );
  }
}

export default BlogPost;
